package main;

import java.util.logging.Logger;

import engine.Detroid;
import tuning.Arena;

public class GenomeTestLauncher {

	public static void main(String[] args) {
		String genome = "";
		Detroid d = new Detroid();
		d.init();
		d.getParameters().initFromGrayCodeString(genome);
		System.out.println(d.getParameters().toString());
		Arena a = new Arena(new Detroid(), Logger.getAnonymousLogger());
		a.match(d, new Detroid(), 200, 500);
		a.close();
	}

}
